import React, {Component} from 'react';
import * as firebase from 'firebase/app';
import firebaseConfig from '../firebaseConfig';


//import { Route, Switch } from 'react-router-dom';
// <Switch>
//   <Route exact path="/" component={JoinEvents} />
//   <Route exact path="/create" component={CreateEvent} />
//   <Route exact path="/settings" component={Settings} />
//   <Route exact path="/about" component={About} />
//   <Route exact path="/profile" component={Profile} />
// </Switch>




class CurrentEvent extends Component {
	constructor(){
		super();
		this.state = {
			"restaurant":"Dominos",
			"time":"3:30pm",
			"Owner":"Jake"
		}
		this.getRegisteredEvents = this.getRegisteredEvents.bind(this);
		this.deleteRegisteredEvent = this.deleteRegisteredEvent.bind(this);
	}
	  getRegisteredEvents(){
    let app = this;
    fetch("https://us-central1-litlunch.cloudfunctions.net/litlunch/users/" + app.state.user_id + "/registered_events/").then(function(res){
      return res.json();
    }).then(function(res){
      console.log(res);

    })
  }

   deleteRegisteredEvent(){
    let app = this;
    fetch("https://us-central1-litlunch.cloudfunctions.net/litlunch/users/" + app.state.user_id + "/registered_events/" + app.state.event_id + "/delete",{
      method: "POST",
       headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization':this.props.user.ra
      }
    }).then(function(res){
      return res.json();
    }).then(function(res){
      console.log(res);

    })
  }


    componentDidMount(){
    let a = this;
      firebase.database().ref("users/"+this.props.user.uid).on("value", function(snap){
        if(snap.val()==null)
          return;
        else
          a.setState({"current_event":snap.val()});
      console.log(snap.val());
      })
    }
  render(){
  	return(<div>
  			<div className = "event_pic"></div>
  				<p>DOMINOS</p>
  				<p>1232 West Flskda</p>
  				<div>
  				<p>Attending</p>
  					<ul className = "current_members">
  						<p>Jerry</p>	
  					</ul>
  				</div>
  				<button onClick = {this.deleteRegisteredEvent}>Cancel</button>
  			</div>);
  }
}
export default CurrentEvent;
