                  <div className = "view-wrap">
                    <div className = "filter_wrap"><div className = "filter_content"></div></div>
                    <div className = "events_wrap">
                      <Events user = {user}/>
                    </div>
                  </div>*/
// </Switch>




class App extends Component {

   componentDidMount(){
    let a = this;
      firebase.database().ref("users/"+this.props.user.uid + "/current_event").on("value", function(snap){
        if(snap.val()==null)
          return;
        else
          a.setState({"current_event":snap.val()});
      console.log(snap.val());
      })
    }

  render() {
            const {
      user,
      signOut,
      signInWithGoogle,
} = this.props;
    return (
      <div>
      {
            user
              ? <div>
                  <CurrentEvent user = {user}/>
                </div>
              : <p>Please sign in.</p>
          }

          {
            user
              ?<div></div>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
    }
    </div>

    );
  }
}
const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);
