import React, { Component } from 'react';
import './App.css';
import Events from './components/Events';
import NaviBar from './components/NaviBar';
import withFirebaseAuth from 'react-with-firebase-auth'
import * as firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/database';

import CurrentEventContainer from './components/CurrentEventContainer';
import CurrentEvent from './components/CurrentEvent';
import firebaseConfig from './firebaseConfig';

import { Route, Switch } from 'react-router-dom';

const firebaseApp = firebase.initializeApp(firebaseConfig);

// <Switch>
//   <Route exact path="/" component={JoinEvents} />
//   <Route exact path="/create" component={CreateEvent} />
//   <Route exact path="/settings" component={Settings} />
//   <Route exact path="/about" component={About} />
//   <Route exact path="/profile" component={Profile} />
/*
<NaviBar user_name = {user.displayName} signOut = {signOut}/>
                  <div className = "my_info_banner"><div id = "location_icon"></div><p style = {{"display": "inline-block","vertical-align": "top;"}}><i>695 Park Ave, New York, NY 10065</i></p></div>
                  <div className = "view-wrap">
                    <div className = "filter_wrap"><div className = "filter_content"></div></div>
                    <div className = "events_wrap">
                      <Events user = {user}/>
                    </div>
                  </div>*/
// </Switch>
/*
                  <div className = "view-wrap">
                    <div className = "filter_wrap"><div className = "filter_content">
                    <Filter submit_filter = {this.submit_filter}/>
                    </div></div>
                    <div className = "restaurants_div">
                      <ul>{restaurants_retrieved_rendered}</ul>
                    </div>
                    <div className = "events_wrap">
                      <CreateEvent restaurant = {this.state.restaurant} location = {this.state.location} user = {user} />
                    </div>
                  </div>
*/



class App extends Component {

  constructor(){
    super();
    this.state = {
      "restaurants":[],
      "location":"",
      "restaurant":""
    }
    this.submit_filter = this.submit_filter.bind(this);
    this.setRestaurant = this.setRestaurant.bind(this);
  }
  setRestaurant(restaurant, location){
    this.setState({"restaurant":restaurant,"location":location});
    console.log(restaurant, location);
  }

  submit_filter(rating, price, zipCode){
    console.log(rating,price,zipCode);
    let a =this;
     fetch("https://safe-headland-63176.herokuapp.com/restaurant_retrieval",{
      method: "POST",
       headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },body: JSON.stringify({zipCode:zipCode,price:price,categories:'Mexican'})
    }).then(function(res){
      return res.json();
    }).then(function(res){
      //app.setState({"events":res});
      a.setState({"restaurants":res});

    })
  }


  render() {
            const {
      user,
      signOut,
      signInWithGoogle,
} = this.props;
let restaurants_retrieved_rendered = [];
for(let restaurant in this.state.restaurants){
  let temp = (<li onClick = {()=>{this.setRestaurant(this.state.restaurants[restaurant].name, this.state.restaurants[restaurant].location)}}><div className = "img" style = {{"background-image":"url(" + this.state.restaurants[restaurant].image_url + ")"}}></div><p>{this.state.restaurants[restaurant].name}</p><p>{this.state.restaurants[restaurant].location}</p></li>);
  restaurants_retrieved_rendered.push(temp);
}
    return (
      <div>
      {
            user
              ? <div>
                  <CurrentEvent user = {user}/>


                  <NaviBar user_name = {user.displayName} signOut = {signOut}/>


                  <div className = "my_info_banner"><div id = "location_icon"></div><p style = {{"display": "inline-block","vertical-align": "top;"}}><i>695 Park Ave, New York, NY 10065</i></p></div>





                </div>
              : <p>Please sign in.</p>
          }

          {
            user
              ?<div></div>
              : <button onClick={signInWithGoogle}>Sign in with Google</button>
    }
    </div>

    );
  }
}
const firebaseAppAuth = firebaseApp.auth();

const providers = {
  googleProvider: new firebase.auth.GoogleAuthProvider(),
};

export default withFirebaseAuth({
  providers,
  firebaseAppAuth,
})(App);
