import React, { Component } from 'react'
import { Input, Menu } from 'semantic-ui-react';
import './navbar_wrap_semantic.scss';

import {Link} from 'react-router-dom';

export default class MenuExampleSecondary extends Component {
  constructor(){
    super();

  }
  state = { activeItem: 'home' }

  handleItemClick = (e, { name }) => {this.setState({ activeItem: name }); this.props.history.push("/create")}

  render() {
    const { activeItem } = this.state

    return (
      <div className = "navbar-wrap-semantic">
      <Menu secondary id = "menu">
        <Menu.Item 
        name='home' 
        active={activeItem === 'home'} 
        onClick={this.handleItemClick} 
        />
        <Menu.Item
          name='create'
          active={activeItem === 'create'}
          onClick={this.handleItemClick}
        />
        <Menu.Item
          name='browse'
          active={activeItem === 'browse'}
          onClick={this.handleItemClick}
        />
        <Menu.Menu position='right'>
          <Menu.Item
            name={this.props.user_name}
            active={activeItem === this.props.user_name}
            onClick={this.handleItemClick}
          />
        </Menu.Menu>
        <Menu.Item 
        name='Sign Out' 
        active={activeItem === 'Sign Out'} 
        onClick={this.props.signOut} 
        />
      </Menu>
      </div>
    )
  }
}
